// Joke.js

import React from "react";
import photo from "../../src/funny.png"

import Button from "./Button";


const Joke = () => {
	const [Joke, setJoke] = React.useState("");

	const fetchApi = () => {
		fetch("https://sv443.net/jokeapi/v2/joke/Programming?type=single")
			.then((res) => res.json())
			.then((data) => setJoke(data.joke));
	};

	return (
            <div class="card" style={{width: "18rem;", alignItems:"center"}}>
            <img class="card-img-top" src={photo} alt="Card image cap" style={{width:"50%" , height:"50"}}></img>
            <div class="card-body">
                <h5 class="card-title">Generate Joke</h5>
                <Button callApi={fetchApi} /> 
                <p>{Joke}</p> 

            </div>
            </div>


	);
}

export default Joke;
