import Button from "../src/Components/Button";
import React from "react";

const Joke = () => {
  const [Joke, setJoke] = React.useState(""); // with use state we need a variable and a setstate variable
  const fetchApi = () => {
    fetch("https://sv443.net/jokeapi/v2/joke/Programming?type=single") // fetch used
      .then((res) => res.json()) // get response in json
      .then((data) => setJoke(data.joke)); // set joke as data
  };

  return (
    <div className="Joke">
      <Button callApi={fetchApi} />
    </div>
  );
};

export default Joke;
