import React from "react";
import photo from "../../src/funny.png";
import Button from "./Button";

const Joke = () => {
    const [joke, setJoke] = React.useState("");

    const fetchApi = () => {
        fetch("https://sv443.net/jokeapi/v2/joke/Programming?type=single")
            .then((res) => res.json())
            .then((data) => setJoke(data.joke));
    };

    return (
        <div className="card" style={{ width: "80%", maxWidth: "500px", display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", textAlign: "center", padding: "20px", borderRadius: "10px", backgroundColor: "rgba(255, 255, 255, 0.8)" }}>
            <img className="card-img-top" src={photo} alt="Card image cap" style={{ width: "20%", height: "20%" }} />
            <div className="card-body">
                <p style={{ backgroundColor: "black", color: "yellow", fontSize: "large", padding: "10px", borderRadius: "5px" }}>{joke}</p>
                <Button callApi={fetchApi} />
            </div>
        </div>
    );
};

export default Joke;
